import React from 'react';
import { Button, Form, FormGroup, Label, Input } from 'reactstrap';
import axios from 'axios';

class AddEditForm extends React.Component {
    state = {
        id: 0,
        customer: '',
        project: '',
        type: '',
        hours: '',
        startdate: '',
        enddate: '',
        description: ''
    }

    onChange = e => {
        this.setState({ [e.target.name]: e.target.value })
    }

    submitFormAdd = e => {
        e.preventDefault()
        axios({
            method: 'post',
            url: 'https://localhost:5001/api/events/',
            data: {
                customer: this.state.customer,
                project: this.state.project,
                type: this.state.type,
                hours: this.state.hours,
                startdate: this.state.startdate,
                enddate: this.state.enddate,
                description: this.state.description
            }
        })
            .then(event => {
                if (Array.isArray(event)) {
                    this.props.addEventToState(event[0])
                    this.props.toggle()
                } else {
                    console.log('failure')
                }
            })
            .catch(err => console.log(err.response))
    }

    submitFormEdit = e => {
        e.preventDefault()
        axios({
            method: 'put',
            url: 'https://localhost:5001/api/events/',
            data: {
                id: this.state.id,
                customer: this.state.customer,
                project: this.state.project,
                type: this.state.type,
                hours: this.state.hours,
                startdate: this.state.startdate,
                enddate: this.state.enddate,
                description: this.state.description
            }
        })
            .then(function (response) {
            console.log(response);
        })
            .then(event => {
                console.log(event)
                console.log(this.state.id)
                if (Array.isArray(event.config.data)) {
                     console.log("ananskm")

                    this.props.updateState(event)
                    this.props.toggle()
                } else {
                    console.log('failure')
                    console.log(event)
                }
            })
            .catch(err => console.log(err))
    }

    componentDidMount() {
        console.log(this.props.event)
        if (this.props.event) {
            const { id, customer, project, type, hours, startdate, enddate, description} = this.props.event
            this.setState({ id, customer, project, type, hours, startdate, enddate, description })

        }

    }

    render() {
        return (
            <Form onSubmit={this.props.event ? this.submitFormEdit : this.submitFormAdd}>
                <FormGroup>
                    <Label for="customer">Customer</Label>
                    <Input type="text" name="customer" id="customer" onChange={this.onChange} value={this.state.customer === null ? '' : this.state.customer} />
                </FormGroup>
                <FormGroup>
                    <Label for="project">Project</Label>
                    <Input type="text" name="project" id="project" onChange={this.onChange} value={this.state.project === null ? '' : this.state.project} />
                </FormGroup>
                <FormGroup>
                    <Label for="type">Type</Label>
                    <Input type="text" name="type" id="type" onChange={this.onChange} value={this.state.type === null ? '' : this.state.type} />
                </FormGroup>
                <FormGroup>
                    <Label for="hours">Hours</Label>
                    <Input type="number" name="hours" id="hours" onChange={this.onChange} value={this.state.hours === null ? '' : this.state.hours} />
                </FormGroup>
                <FormGroup>
                    <Label for="description">Description</Label>
                    <Input type="text" name="description" id="description" onChange={this.onChange} value={this.state.description === null ? '' : this.state.description} />
                </FormGroup>
                <FormGroup>
                    <Label for="startdate">StartDate</Label>
                    <Input type="datetime-local" name="startdate" id="startdate" onChange={this.onChange} value={this.state.startdate} placeholder= "2020-11-29T19:10:25" />
                </FormGroup>
                <FormGroup>
                    <Label for="enddate">EndDate</Label>
                    <Input type="datetime-local" name="enddate" id="enddate" onChange={this.onChange} value={this.state.enddate} placeholder= "2020-11-29T19:10:25" />
                </FormGroup>
                <Button>Submit</Button>
            </Form>
        );
    }
}

export default AddEditForm
