import React, { Component } from 'react'
import { Table, Button } from 'reactstrap';
import ModalForm from './Modal'

class DataTable extends Component {

    deleteItem = id => {
        let confirmDelete = window.confirm('Delete item forever?')
        if (confirmDelete) {
            fetch('http://localhost:5001/api/events', {
                method: 'delete',
                headers: {
                    'Content-Type': 'application/json'
                },
                body: JSON.stringify({
                    id
                })
            })
                .then(response => response.json())
                .then(item => {
                    this.props.deleteEventFromState(id)
                })
                .catch(err => console.log(err))
        }

    }

    render() {

        const items = this.props.currentEvents.map(event => {
            return (
                <tr key={event.id}>
                    <th scope="row">{event.id}</th>
                    <td>{event.customer}</td>
                    <td>{event.project}</td>
                    <td>{event.type}</td>
                    <td>{event.description}</td>
                    <td>{event.startdate}</td>
                    <td>{event.enddate}</td>
                    <td>
                        <div style={{ width: "110px" }}>
                            <ModalForm buttonLabel="Edit" item={event} updateState={this.props.updateState} />
                            {' '}
                            <Button color="danger" onClick={() => this.deleteItem(event.id)}>Del</Button>
                        </div>
                    </td>
                </tr>
            )
        })

        return (
            <Table responsive hover>
                <thead>
                    <tr>
                        <th>ID</th>
                        <th>Customer</th>
                        <th>Project</th>
                        <th>Type</th>
                        <th>Description</th>
                        <th>StartDate</th>
                        <th>EndDate</th>
                        <th>Actions</th>
                    </tr>
                </thead>
                <tbody>
                    {currentEvents}
                </tbody>
            </Table>
        )
    }
}

export default DataTable
