import React from 'react'
import FullCalendar, { formatDate } from '@fullcalendar/react'
import dayGridPlugin from '@fullcalendar/daygrid'
import timeGridPlugin from '@fullcalendar/timegrid'
import interactionPlugin from '@fullcalendar/interaction'
import { Container, Row, Col, Button, Modal, ModalHeader, ModalBody } from 'reactstrap'
import DataTable from './components/DataTable'
import { CSVLink } from "react-csv"
import axios from 'axios';
import ModalForm from './components/Modal'
import ReactDOM from 'react-dom';
import 'bootstrap/dist/css/bootstrap.min.css';
import 'bootstrap/dist/js/bootstrap.min.js';
//jquery, popper.js libraries for bootstrap modal
import 'jquery/dist/jquery.min.js';
import 'popper.js/dist/umd/popper.min.js'
import $ from 'jquery';
import AddEditForm from './components/FormAddEdit'


export default class DemoApp extends React.Component {


    state = {
        modal: false,
        weekendsVisible: true,
        currentEvents: []
    }

    /*  getEvents() {
          fetch('https://localhost:5001/api/events/')
              .then(response => response.json())
              .then(currentEvents => this.setState({ currentEvents }))
              .catch(err => console.log(err))
      }
      */



    addEventToState = (event) => {
        this.setState(prevState => ({
            currentEvents: [...prevState.currentEvents, event]
        }))
    }

    toggle = () => {
        this.setState({ modal: !this.state.modal });
    };

    updateState = (event) => {
        const eventIndex = this.state.currentEvents.findIndex(data => data.id === event.id)
        const newArray = [
            // destructure all items from beginning to the indexed item
            ...this.state.currentEvents.slice(0, eventIndex),
            // add the updated item to the array
            event,
            // add the rest of the items to the array from the index after the replaced item
            ...this.state.currentEvents.slice(eventIndex + 1)
        ]
        this.setState({ currentEvents: newArray })
    }

    deleteItem = id => {
        let confirmDelete  /*window.confirm('Delete event forever?')*/;
        console.log(id)
        if (confirmDelete) {
            const url = "https://localhost:5001/api/events/DeleteEventDetails/" + id;

            axios
                .delete(url)
                .then(item => {
                    this.props.deleteEventFromState(id)
                })
                .catch(err => {
                    console.log(err.response);
                });
        };
    }

    deleteEventFromState = (id) => {
        const updatedEvents = this.state.currentEvents.filter(event => event.id !== id)
        this.setState({ currentEvents: updatedEvents })
    }

    async componentDidMount() {
        await axios.get('/api/events')
            .then(response => {
                this.setState({ currentEvents: response.data })
                console.log({ calendarEvents: response.data })
            })
            .catch(function (error) {
                console.log(error);
            })
    }

    render() {

        return ( this.renderCalendar())
    }




renderTahta() {
        return (
            <div>

                <Container className="DemoApp">
                    <Row>
                        <Col>
                            <h1 style={{ margin: "20px 0" }}>Event Database</h1>
                        </Col>
                    </Row>
                    <Row>
                        <Col>
                            <DataTable currentEvents={this.state.currentEvents} updateState={this.updateState} deleteEventFromState={this.deleteEventFromState} />
                        </Col>
                    </Row>
                    <Row>
                        <Col>
                            <CSVLink
                                filename={"db.csv"}
                                color="primary"
                                style={{ float: "left", marginRight: "10px" }}
                                className="btn btn-primary"
                                data={this.state.currentEvents}>
                                Download CSV
                            </CSVLink>
                            <ModalForm buttonLabel="Add Event" addEventToState={this.addEventToState} />
                        </Col>
                    </Row>
                </Container>
            </div>



        )
}

    renderCalendar() {
        const closeBtn = <button className="close" onClick={this.toggle}>&times;</button>
        return (
            <div className='demo-app'>
                {/*this.renderSidebar()*/}
                <ModalForm id="Modalarda" buttonLabel="ananabas" addEventToState={this.addEventToState} />
                <div className='demo-app-main'>
                    <FullCalendar
                        plugins={[dayGridPlugin, timeGridPlugin, interactionPlugin]}
                        headerToolbar={{
                            left: 'prev,next today',
                            center: 'title',
                            right: 'dayGridMonth,timeGridWeek,timeGridDay'
                        }}
                        initialView='dayGridMonth'
                        editable={true}
                        selectable={true}
                        selectMirror={true}
                        dayMaxEvents={true}
                        weekends={this.state.weekendsVisible}
                        /*initialEvents={INITIAL_EVENTS} */ /* alternatively, use the `events` setting to fetch from a feed*/
                        events='api/events/'
                        select={this.handleDateSelect}
                        dateClick={(e) => {
                            console.log(e.dateStr)
                            this.toggle()
                        }

                        }
                   // $("#Modalarda").onClick();
                            //$(".modal-body").html("<h3>" + "ANKARAGÜCÜ" + "</h3>");

                        eventContent={renderEventContent} /* custom render function */
                        eventClick={this.deleteItem}

                        eventsSet={this.handleEvents} // called after events are initialized/added/changed/removed

                    /* you can update a remote database when these fire: eventAdd={function(){}} eventChange={function(){}}eventRemove={function(){}}*/




                    />
                    <Modal isOpen={this.state.modal} toggle={this.toggle} className={this.props.className}>
                        <ModalHeader toggle={this.toggle} close={closeBtn}>Add New Event</ModalHeader>
                        <ModalBody>
                            <AddEditForm
                                addEventToState={this.addEventToState}
                                updateState={this.updateState}
                                toggle={this.toggle}
                                event={this.event} />
                        </ModalBody>
                    </Modal>
                </div>

                </div>

        )


            }

    renderSidebar() {
        return (
            <div className='demo-app-sidebar'>
                <div className='demo-app-sidebar-section'>
                    <h2>Instructions</h2>
                    <ul>
                        <li>Select dates and you will be prompted to create a new event</li>
                        <li>Drag, drop, and resize events</li>
                        <li>Click an event to delete it</li>
                    </ul>
                </div>
                <div className='demo-app-sidebar-section'>
                    <label>
                        <input
                            type='checkbox'
                            checked={this.state.weekendsVisible}
                            onChange={this.handleWeekendsToggle}
                        ></input>
                        toggle weekends
          </label>
                </div>
                <div className='demo-app-sidebar-section'>
                    <h2>All Events ({this.state.currentEvents.length})</h2>
                    <ul>
                        {this.state.currentEvents.map(renderSidebarEvent)}
                    </ul>
                </div>
            </div>
        )
    }

    handleWeekendsToggle = () => {
        this.setState({
            weekendsVisible: !this.state.weekendsVisible
        })
    }

    handleDateSelect = (selectInfo) => {

        console.log(selectInfo.startStr)
        //let description = prompt('Please enter a new title for your event')
        let calendarApi = selectInfo.view.calendar

        calendarApi.unselect() // clear date selection

        if (false) {
            calendarApi.addEvent({


                start: selectInfo.startStr,
                end: selectInfo.endStr,
                allDay: true
            })
        }
       /* this.addEventToState()*/
    }




    handleEventClick = (clickInfo) => {
        console.log(clickInfo.event.day)

        if (window.confirm(`Are you sure you want to delete the event '${clickInfo.event.extendedProps.description}'`)) {

            clickInfo.event.remove()
        }
    }

    handleEvents = (events) => {
        this.setState({
            currentEvents: events
        })
    }

}

function renderEventContent(eventInfo) {
    return (

        <>


            <i>{eventInfo.event.extendedProps.description}</i>
        </>
    )
}
function renderSidebarEvent(event) {
    console.log(event)
    return (
        <li key={event.id}>
            <b>{formatDate(event.start, { year: 'numeric', month: 'short', day: 'numeric' })}</b>
            <i>{event.description}</i>
        </li>
    )
}
