import React from 'react';
import { Button, Form, FormGroup, Label, Input } from 'reactstrap';

class AddEditForm extends React.Component {
    state = {
        id: 0,
        customer: '',
        project: '',
        type: '',
        hours: 0,
        description: '',
        startdate: '',
        enddate:'',
    }

    onChange = e => {
        this.setState({ [e.target.name]: e.target.value })
    }

    submitFormAdd = e => {
        e.preventDefault()
        fetch('http://localhost:5001/api/events', {
            method: 'post',
            headers: {
                'Content-Type': 'application/json'
            },
            body: JSON.stringify({
                customer: this.state.customer,
                project: this.state.project,
                type: this.state.type,
                hours: this.state.hours,
                startdate: this.state.startdate,
                enddate: this.state.enddate,
                description: this.state.description
            })
        })
            .then(response => response.json())
            .then(event => {
                if (Array.isArray(event)) {
                    this.props.addEventToState(event[0])
                    this.props.toggle()
                } else {
                    console.log('failure')
                }
            })
            .catch(err => console.log(err))
    }

    submitFormEdit = e => {
        e.preventDefault()
        fetch('http://localhost:5001/api/events', {
            method: 'put',
            headers: {
                'Content-Type': 'application/json'
            },
            body: JSON.stringify({
                id: this.state.id,
                customer: this.state.customer,
                project: this.state.project,
                type: this.state.type,
                hours: this.state.hours,
                startdate: this.state.startdate,
                enddate: this.state.enddate,
                description: this.state.description
            })
        })
            .then(response => response.json())
            .then(event => {
                if (Array.isArray(event)) {
                    // console.log(item[0])
                    this.props.updateState(event[0])
                    this.props.toggle()
                } else {
                    console.log('failure')
                }
            })
            .catch(err => console.log(err))
    }

    componentDidMount() {
        // if item exists, populate the state with proper data
        if (this.props.event) {
            const { id, customer, project, type, hours, startdate, enddate, description } = this.props.event
            this.setState({ id, customer, project, type, hours, startdate, enddate, description })
        }
    }

    render() {
        return (
            <Form onSubmit={this.props.event ? this.submitFormEdit : this.submitFormAdd}>
                <FormGroup>
                    <Label for="customer">Customer</Label>
                    <Input type="text" name="customer" id="customer" onChange={this.onChange} value={this.state.customer === null ? '' : this.state.customer} />
                </FormGroup>
                <FormGroup>
                    <Label for="project">Project</Label>
                    <Input type="text" name="project" id="project" onChange={this.onChange} value={this.state.project === null ? '' : this.state.project} />
                </FormGroup>
                <FormGroup>
                    <Label for="type">Type</Label>
                    <Input type="email" name="type" id="type" onChange={this.onChange} value={this.state.type === null ? '' : this.state.type} />
                </FormGroup>
                <FormGroup>
                    <Label for="hours">Hours</Label>
                    <Input type="text" name="hours" id="hours" onChange={this.onChange} value={this.state.hours === null ? '' : this.state.hours} />
                </FormGroup>
                <FormGroup>
                    <Label for="description">Description</Label>
                    <Input type="text" name="description" id="description" onChange={this.onChange} value={this.state.description === null ? '' : this.state.description} />
                </FormGroup>
                <FormGroup>
                    <Label for="startdate">StartDate</Label>
                    <Input type="text" name="startdate" id="startdate" onChange={this.onChange} value={this.state.startdate} placeholder= "2020-11-29T19:10:25" />
                </FormGroup>
                <FormGroup>
                    <Label for="enddate">EndDate</Label>
                    <Input type="text" name="enddate" id="enddate" onChange={this.onChange} value={this.state.enddate} placeholder= "2020-11-29T19:10:25" />
                </FormGroup>
                <Button>Submit</Button>
            </Form>
        );
    }
}

export default AddEditForm
